import React from "react";
import { ChartBar } from "./ChartBar";
import './Chart.css';
export const Chart = (props) => {
    console.log(parseInt(props.dataPoints.amount));
  const amounts = props.dataPoints.map((item) => {
    return +item.amount;
  });
  const maxValue = Math.max(...amounts);
  const points = [
    { label: "JAN", value: 0 },
    { label: "FEB", value: 0 },
    { label: "MAR", value: 0 },
    { label: "APR", value: 0 },
    { label: "MAY", value: 0 },
    { label: "JUN", value: 0 },
    { label: "JUL", value: 0 },
    { label: "AUG", value: 0 },
    { label: "SEP", value: 0 },
    { label: "OCT", value: 0 },
    { label: "NOV", value: 0 },
    { label: "DEC", value: 0 },
  ];
  for (let item of props.dataPoints) {
    let date = new Date(item.date);
    let index = date.getMonth();
    points[index].value = points[index].value + +item.amount;
  }
  return (
    <div className="mainChart">
      {points.map((dataPoint) => {
        return (
          <ChartBar
            value={dataPoint.value}
            label={dataPoint.label}
            maxValue={maxValue}
            id={dataPoint.label}
          />
        );
      })}
    </div>
  );
};
