import React from "react";
import styles from './chs'

export const ChartBar = (props) => {
  let value = "0%";
  if (props.maxValue > 0) {
    value = (props.value / props.maxValue) * 100 + "%";
  }
  return (
    <>
      <div className="barMain">
        <div className="chart_outer">
          <div className="chartBar" style={{ height: value }}></div>
        </div>
        <p className="label">{props.label}</p>
      </div>
    </>
  );
};
