import logo from "./logo.svg";
import "./components/ExpenseItem.css";

import "./App.css";
import ExpenseItem from "./components/ExpenseItem";
import { Card } from "./components/Card";
import { ExpenseForm } from "./components/ExpenseForm";
import { useState } from "react";
import { ExpenseFilter } from "./components/ExpenseFilter";

function App() {
  const [expenses, setExpenses] = useState(
    JSON.parse(localStorage.getItem("expenses"))
  );
  const [year, setYear] = useState("all");
  const [filteredItems, setFilteredItems] = useState([]);
  const func1 = (expenseData) => {
    const data = { ...expenseData, id: Math.random().toString() };
    setExpenses((prevState) => {
      if (prevState) {
        localStorage.setItem("expenses", JSON.stringify([...prevState, data]));
        return [...prevState, data];
      } else {
        localStorage.setItem("expenses", JSON.stringify([data]));
        return [data];
      }
    });
  };
  const handleYearChange = (year) => {
    setYear(year);
    setFilteredItems(() => {
      return expenses.filter((item) => {
        const date = new Date(item.date);
        return date.getFullYear.toString() == 2021;
      });
    });
  };
  return (
    <>
      <ExpenseForm onApp={func1} />
      <Card>
        <ExpenseFilter handleYearChange={handleYearChange} />
        {year == "all"
          ? expenses?.map((prod) => {
              return <ExpenseItem key={prod.id} prod={prod} />;
            })
          : filteredItems?.map((prod) => {
              return <ExpenseItem key={prod.id} prod={prod} />;
            })}
      </Card>
    </>
  );
}

export default App;
